import heapq
from typing import List

class Solution:
    def mostBooked(self, n: int, meetings: List[List[int]]) -> int:
        # ‚ùå Implement your solution here


        usedRooms = []
        availableRooms = [i for i in range(n)]
        usedRoomsCount = [0] * n
        meetings.sort(key=lambda x: x[0])

        for start, endTime in meetings:

            while usedRooms and usedRooms[0][0] <= start:
                ending,room = heapq.heappop(usedRooms)
                heapq.heappush(availableRooms, room)

            if not availableRooms:
                availableEnd, room = heapq.heappop(usedRooms)
                endTime = availableEnd + (endTime - start)
                heapq.heappush(availableRooms,room)

            room = heapq.heappop(availableRooms)
            heapq.heappush(usedRooms,(endTime, room))
            usedRoomsCount[room] += 1

        return usedRoomsCount.index(max(usedRoomsCount))





